sqlMap.select:
select aadneauthip.* ,authip.ipaddr from ( select aadneauth.* ,authmode.auth_type from ( select aadne.* ,c.merchant from ( select aad.*, ane.hardware_ver, ane.software_ver, ane.gwip
from ( SELECT a.id, a.NAME, a.mac, a.remote_addr, a.sys_memfree, a.update_time, a.last_seen, a.redirect_url, a.portal_url, a.sys_upload, a.created_at, a.updated_at, a.configflag, a.cmdline, a.time_limit, a.lat, a.developer, a.cmdflag, a.long, a.sys_uptime, a.ssid, ad.province, ad.city, ad.district, ad.detail FROM access_nodes a LEFT JOIN addresses ad ON a.id = ad.access_node_id
a.id = #id#
) aad LEFT JOIN access_nodes_exp ane ON aad.id = ane.access_node_id ) aadne LEFT JOIN contacts c on aadne.id = c.access_node_id ) aadneauth left join auths authmode on aadneauth.id = authmode.access_node_id
) aadneauthip LEFT JOIN cloudwifi_manage.authservers authip on aadneauthip.mac = authip.node_mac order by aadneauthip.created_at desc ;
